@use "../abstracts/" as *;

html {
  font-size: 62.5%;
  box-sizing: border-box;
}

*,
*:before,
*:after {
  margin: 0;
  padding: 0;
  box-sizing: inherit;
}

body {
  font-size: 1.6rem;
}

img {
  width: 100%;
  vertical-align: middle;
}

ul {
  list-style: none;
}

a {
  text-decoration: none;

  &:visited,
  &:active {
    color: var(--color-blue-desaturated);
  }
}

.hidden {
  display: none;

  @include for-desktop {
    display: block;
  }
}

