@use "../abstracts/" as *;

.hero {
  position: relative;
}

.header {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;

  display: flex;
  justify-content: space-between;
  align-items: center;

  padding-top: 3rem;
  padding-inline: 2rem;

  @include for-desktop {
    padding-inline: 3rem;
  }
}

.logo {
  width: 12rem;
}

.btn-meun {
  position: relative;

  width: 2rem;
  height: 1px;
  background-color: var(--color-white);

  @include for-desktop {
    display: none;
  }

  // Creating the upper and lower line
  &:before,
  &:after {
    content: "";
    position: absolute;
    left: 0;

    width: 100%;
    height: 1px;

    background-color: var(--color-white);
  }

  &:before {
    top: calc(0rem - var(--burger-meun-space));
  }

  &:after {
    top: var(--burger-meun-space);
  }
}

.nav {
  position: absolute;
  top: 10rem;
  left: 50%;
  z-index: 100;

  width: calc(100% - 4rem);

  font-family: var(--ff-san-ser);

  box-shadow: 0 5px 2rem 2px rgba(rgb(23, 21, 36), 0.3);
  transform: translateX(-50%);

  @include for-desktop {
    position: initial;

    width: auto;

    box-shadow: none;
    transform: translateX(0);
  }

  // Triangle making
  &:before {
    content: "";
    position: absolute;
    top: 0;
    right: 0;

    border-top: 1rem solid transparent;
    border-right: 1rem solid var(--color-white);
    border-bottom: 1rem solid var(--color-white);
    border-left: 1rem solid transparent;

    transform: translateY(-100%);

    @include for-desktop {
      display: none;
    }
  }

  &__item {
    padding: 1rem 2rem;
    border-radius: var(--border-radius-sm);
    transition: 0.2s;

    @include hover {
      &:hover {
        background-color: rgba(white, 0.2);

        font-family: var(--ff-ser);
      }
    }
  }

  &__list {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 2.5rem;

    height: 50vh;

    background-color: var(--color-white);

    @include for-desktop {
      flex-direction: row;
      justify-content: end;
      background-color: transparent;
      height: auto;

      gap: 5rem;
    }
  }

  &__link:visited,
  &__link:active {
    color: var(--color-blue-grayish-2);

    @include for-desktop {
      color: var(--color-white);
    }
  }

  &__item--active {
    background-color: var(--color-primary-yellow);
    padding: 1rem 2rem;
    border-radius: var(--border-radius-sm);

    font-family: var(--ff-ser);

    @include for-desktop {
      background-color: var(--color-white);
    }

    .nav__link:visited,
    .nav__link:active {
      color: var(--color-blue-desaturated);
    }
  }
}

.slogan {
  position: absolute;
  top: 30%;
  left: 0;
  right: 0;

  margin: auto;

  font-size: 3.5rem;
  color: var(--color-white);

  @include for-desktop {
    top: 25%;
    right: 0;
    margin: auto;

    font-size: 4rem;
  }
}

.img__arrow--down {
  position: absolute;
  top: 55%;
  left: 50%;

  width: 3rem;

  transform: translateX(-50%);

  @include for-desktop {
    top: 45%;

    width: 2.5rem;
  }
}

